import React from "react";
import pass from "./password.png";
import "./Login.css";
import { useState, useEffect } from "react";
import Axios from "axios";
import { useNavigate, NavLink } from "react-router-dom";
import App from "./App";
import { TypeAnimation } from "react-type-animation";

function Login() {
  const navigate = useNavigate();
  const [usname, Setusname] = useState("");
  const [passname, Setpassname] = useState("");

  const [userList, setuserList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3001/userdet").then((response) => {
      setuserList(response.data);
      console.log(response.data);
    });
  }, []);
  // console.log(userList);
  const putdata = () => {
    let count = 0;
    for (let i = 0; i < userList.length; i++) {
      if (userList[i].Username === usname) {
        if (userList[i].Password === passname) {
          count = 1;
        }
      }
      if (count === 1) {
        navigate("/App", { state: usname });
      } else {
        navigate("/Login");
      }
    }
  };

  return (
    <div className="outer-login">
      <div className="outer-login-text">
        <p>
          <img src={pass} />
          <p>Password Manager</p>
        </p>
        <TypeAnimation
          sequence={[
            "We Store Passwords !!",
            1000,
            "We Manage Passwords !!",
            2000,
            "We Encrypt Passwords !!",
            () => {},
          ]}
          wrapper="div"
          cursor={true}
          repeat={Infinity}
          style={{
            fontSize: "2em",
            fontWeight: "10",
            color: "black",
          }}
        />
      </div>
      <div className="inner-login">
        {/* <div className="upper-login">
          <img src={pass} alt="Passman icon" />
          <div className="up"> Pass-Man </div>
        </div> */}
        <div className="login-nam">LOG IN</div>

        <div className="login-email">
          <p>Username</p>
          <input
            type="text"
            className="uname"
            // placeholder="Enter Username"
            onChange={(event) => {
              Setusname(event.target.value);
            }}
          />
        </div>
        <div className="login-password">
          <p>Password</p>
          <input
            type="password"
            className="pass"
            // placeholder="Enter Password"
            onChange={(event) => {
              Setpassname(event.target.value);
            }}
          />
        </div>
        <div className="button-wrapper">
          <button className="login-but" onClick={putdata}>
            LOGIN
          </button>
        </div>
        <div className="login-nam1">
          Don't have an account?{" "}
          <NavLink
            to="/Signup"
            style={{
              color: "#674fa2",
              textDecoration: "none",
              fontWeight: "500",
            }}
          >
            Click here
          </NavLink>
        </div>
      </div>
    </div>
  );
}

export default Login;
